mapper.update:
UPDATE charge_order
SET
    pay_status = #{payStatus},
    pay_channel = #{payChannel},
    pay_time = #{payTime}
WHERE id = #{id}

mapper.select:
SELECT id, order_no, in_record_id, amount, pay_status, pay_channel, pay_time, out_time, fee_rule_name, create_time, update_time FROM charge_order
AND pay_status = #{payStatus}
ORDER BY create_time DESC LIMIT #{offset}, #{limit}
