mapper.update:
UPDATE discount_rule
rule_name = #{ruleName}, description = #{description}, discount_type = #{discountType}, discount_value = #{discountValue}, max_discount = #{maxDiscount}, status = #{status}, effective_start = #{effectiveStart}, effective_end = #{effectiveEnd}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, rule_code, rule_name, description, discount_type, discount_value, max_discount, status, effective_start, effective_end, create_time, update_time FROM discount_rule
AND status = #{status}
ORDER BY create_time DESC
